import { Link, useNavigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { token, setToken } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
    navigate("/login");
  };

  return (
    <nav className="bg-white/10 backdrop-blur-md text-white p-4 rounded-b-2xl shadow-md flex justify-between items-center px-8">
      <h1 className="text-2xl font-semibold tracking-wider">ðŸ’¸ Expense Tracker</h1>
      <div className="flex gap-6 text-sm font-medium">
        <Link to="/" className="hover:text-blue-400 transition">Dashboard</Link>
        <Link to="/add" className="hover:text-blue-400 transition">Add Expense</Link>
        {!token ? (
          <>
            <Link to="/login" className="hover:text-blue-400 transition">Login</Link>
            <Link to="/register" className="hover:text-blue-400 transition">Register</Link>
          </>
        ) : (
          <button
            onClick={handleLogout}
            className="hover:text-red-400 transition"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
